create table "PROJECT" (
	"PROJECT_ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,
	"COUNTRY_ID" INTEGER NOT NULL,
	"CODE" VARCHAR NOT NULL,
	"NAME" VARCHAR NOT NULL);
alter table "PROJECT" add constraint "PROJECT_FK_COUNTRY"
	foreign key("COUNTRY_ID") references "COUNTRY"("COUNTY_ID") on update NO ACTION on delete NO ACTION;