CREATE TABLE "PROJECT" (
	"PROJECT_ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL CONSTRAINT "PROJECT_PK" PRIMARY KEY,
	"COUNTRY_ID" INTEGER NOT NULL,
	CONSTRAINT "PROJECT_FK_COUNTRY"	FOREIGN KEY("COUNTRY_ID") REFERENCES "COUNTRY",
	"CODE" VARCHAR(6) NOT NULL,
	"NAME" VARCHAR(64));