create table "TIME_ENTRY" (
	"TIME_ENTRY_ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,
	"PSP_ASSIGNMENT_ID" INTEGER NOT NULL,
	"DATE" DATE NOT NULL,
	"DURATION" REAL NOT NULL,
	"COMMENT" VARCHAR);
alter table "TIME_ENTRY" add constraint "TIME_ENTRY_FK_PSP_ASSIGNMENT"
	foreign key("PSP_ASSIGNMENT_ID") references "PSP_ASSIGNMENT"("PSP_ASSIGNMENT_ID") on update NO ACTION on delete NO ACTION;
