create table "TASK" (
	"TASK_ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,
	"NAME" VARCHAR NOT NULL,
	"PSP_ASSIGNMENT_ID" INTEGER,
	"PARENT_TASK_ID" INTEGER);
alter table "TASK" add constraint "TASK_FK_PSP_ASSIGNMENT"
	foreign key("PSP_ASSIGNMENT_ID") references "PSP_ASSIGNMENT"("PSP_ASSIGNMENT_ID") on update NO ACTION on delete NO ACTION;
alter table "TASK" add constraint "TASK_FK_TASK"
	foreign key("PARENT_TASK_ID") references "TASK"("TASK_ID") on update NO ACTION on delete NO ACTION;