create table "PERIOD" (
	"PERIOD_ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,
	"TASK_ID" INTEGER NOT NULL,
	"START_TIME" TIMESTAMP NOT NULL,
	"END_TIME" TIMESTAMP NOT NULL);
alter table "PERIOD" add constraint "PERIOD_FK_TASK"
	foreign key("TASK_ID") references "TASK"("TASK_ID") on update NO ACTION on delete NO ACTION;